import { loginSuccess, loginFailure ,setFirstName, setLastName } from './userReducer';
export function login(email, password) {
  return async (dispatch) => {
    try {
      const response = await fetch('http://localhost:3001/api/v1/user/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password })
      });

      if (!response.ok) {
        throw new Error('Login failed');
      }

      const data = await response.json();
      console.log(data);
      const { firstName, lastName, token } = data.body;
      dispatch(loginSuccess({ firstName, lastName, token }));
      return { payload: { token } };
    } catch (error) {
      dispatch(loginFailure(error.message));
    }
  };
}
export function getProfile(token) {
  return async (dispatch) => {
    try {
      const response = await fetch('http://localhost:3001/api/v1/user/profile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}` // Ajoutez le token d'authentification dans l'en-tête de la requête
        },
      });

      if (!response.ok) {
        throw new Error('Failed to get profile data');
      }

      const data = await response.json();
      const { firstName, lastName, email } = data.body;
      dispatch(setFirstName({ firstName, lastName, email }));
      dispatch(setLastName({ firstName, lastName, email }));
    } catch (error) {
      // Gérez les erreurs ici
    }
  };
}
