const initialState = {
    email: null,
    token: null,
    error: null,
    userName: null
  };
  
  export default function userReducer(state = initialState, action) {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          email: action.payload.email,
          token: action.payload.token,
          userName: action.payload.userName,
          error: null
        };
      case 'LOGIN_FAILURE':
        return {
          ...state,
          email: null,
          token: null,
          isLoggedIn: false,
          userName: null,
          error: action.payload
        };
      case 'LOGOUT':
        return {
          ...state,
          email: null,
          token: null,
          isLoggedIn: false,
          userName: null,
          error: null
        };
      default:
        return state;
    }
  }
