import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import Logo from '../../Assets/argentBankLogo.png';
import  './Header.css';
function Header(props) {
  const { firstName, token } = props; // access firstName and token from props
  console.log(firstName)
  return (
    <nav className="main-nav">
      <Link className="main-nav-logo" to="/">
        <img
          className="main-nav-logo-image"
          src={Logo}
          alt="Argent Bank Logo"
        />
        <h1 className="sr-only">Argent Bank</h1>
      </Link>
      <div>
        {token ? (
           <div className="main-nav-item">
            <i className="fa fa-user-circle"></i>
              {firstName}
             <Link to="/" className="sign-out-link">
               <i className="fa fa-sign-out"></i>Sign Out
             </Link>
           </div>
        ) : (
          <Link className="main-nav-item" to="/sign-in">
            <i className="fa fa-user-circle"></i>
            Sign In
          </Link>
        )}
      </div>
    </nav>
  );
}
const mapStateToProps = (state) => {
  return {
    firstName: state?.user?.firstName, // access firstName from state
    token: state?.user?.token // access token from state
  };
};

export default connect(mapStateToProps)(Header); // connect Header to Redux
