const initialState = {
    username: null,
    token: null,
    error: null
  };
  
  export default function userReducer(state = initialState, action) {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          username: action.payload.username,
          token: action.payload.token,
          error: null
        };
      case 'LOGIN_FAILURE':
        return {
          ...state,
          username: null,
          token: null,
          error: action.payload
        };
      case 'LOGOUT':
        return {
          ...state,
          username: null,
          token: null,
          error: null
        };
      default:
        return state;
    }
  }
