import React, { useState } from "react";
import "../../Pages/main.css";
import { NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { setUserName } from "../../User";
const Accounts = [
  {
    id: 1,
    title: "Argent Bank Checking (x8349)",
    amount: "$48,098.43",
    description:"Available Balance",
  },
  {
    id: 2,
    title: "Argent Bank Savings (x6712)",
    amount: "$48,098.43",
    description:"Available Balance",
  },
  {
    id: 3,
    title: "Argent Bank Credit Card (x8349)",
    amount: "$48,098.43",
    description:"Available Balance",
  },
];
function EditUser({title, amount, description}) {
    const dispatch = useDispatch();
    const token = useSelector((state) => state.user.token);
    const [newUsername, setNewUsername] = useState(""); 
    const handleChange = (e) => {
      setNewUsername(e.target.value); 
    };
    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        const response = await fetch("http://localhost:3001/api/v1/user/profile", {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}` // Ajoutez le token d'authentification dans l'en-tête de la requête
          },
          body: JSON.stringify({ userName: newUsername }) // Nouveau nom d'utilisateur
        });
  
        if (!response.ok) {
          throw new Error("Failed to update user profile");
        }
  
        const data = await response.json();
        dispatch(setUserName(data.body.userName));
        setNewUsername(""); 
      } catch (error) {
        console.error(error);
      }
    };
    return(
<>
      <div className="sign-in-content">
        <h1>Edit user info</h1>
        <form onSubmit={handleSubmit}>
          <div className="input-user">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={newUsername} // Utilise la valeur de l'état local pour afficher le nouveau nom d'utilisateur
              onChange={handleChange}
            />
          </div>
          <div className="input-user">
            <label htmlFor="firstname">First name:</label>
            <input disabled="disabled" type="firstname" id="username" />
          </div>
          <div className="input-user">
            <label htmlFor="lastname">Last name:</label>
            <input disabled="disabled" type="lastname" id="username" />
          </div>
          <div className="row">
          <button className="edit-button">Save</button>
          <button className="edit-button">Cancel</button>
          </div>
        </form>
      </div>
      <div className="column">
        {Accounts.map((account) => (
          <div className="darkField" key={account.id}>
            <div className="left">
              <h3 className="account-title">{account.title}</h3>
              <p className="account-amount">{account.amount}</p>
              <p className="account-amount-description">{account.description}</p>
            </div>
            <NavLink to="/Transaction">
              <i className="fa-solid fa-chevron-right"></i>
            </NavLink>
          </div>
        ))}
      </div>
    </>
  );
}
    export default EditUser;
