export function login(email, password) {
    return async (dispatch) => {
      try {
        const response = await fetch('http://localhost:3001/api/v1/user/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ email, password })
        });
  
        if (!response.ok) {
          throw new Error('Login failed');
        }
  
        const data = await response.json();
        const userData = {
          email: data.email,
          token: data.token,
          username: data.username
        };
  
        dispatch({ type: 'LOGIN_SUCCESS', payload: userData });
        const profileResponse = await fetch('http://localhost:3001/api/v1/user/profile', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${data.token}`,
          'Content-Type': 'application/json'
        }
      });

      // Traiter la réponse de la requête de récupération du profil
      if (profileResponse.ok) {
        const profileData = await profileResponse.json();
        
        // Dispatch une action pour mettre à jour les informations du profil dans le state si nécessaire
      } else {
        // Gérer l'erreur si la requête de récupération du profil échoue
      }
      } catch (error) {
        dispatch({ type: 'LOGIN_FAILURE', payload: error.message });
      }
    };
  }
  
  export function logout() {
    return { type: 'LOGOUT' };
  }
